class Solution {
private:
    int rec(int idx,int prev,vector<int>& nums,unordered_map<int,unordered_map<int,int>> &mp){
        if(idx==nums.size()){
            return 1;
        }
        if(mp[idx].find(prev)!=mp[idx].end()){
            return mp[idx][prev];
        }
        int maxVal=nums[idx];
        int prev2=INT_MAX;
        if(idx!=0){prev2=nums[idx-1]-prev;}
        int mod=1e9+7;
        int ans=0;
        for(int j=prev;j<=maxVal;j++){
            if(maxVal-j<=prev2){
                ans=(ans+rec(idx+1,j,nums,mp))%mod;
            }
        }
        return mp[idx][prev]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        unordered_map<int,unordered_map<int,int>> mp;
        return rec(0,0,nums,mp);
    }
};
