const int MOD=1000000007;
class Solution {
public:
int dp[2001][53][53];

long long solve(vector<int>&nums,int ind,int p1,int p2){

    if(ind>=nums.size())    return 1;
    if(dp[ind][p1+1][p2+1]!=-1)    return dp[ind][p1+1][p2+1];
    long long res=0;
    int curr=nums[ind];

    for(int i=0;i<=curr;i++){
        int c1=i,c2=nums[ind]-i;
        if((p1==-1&&p2==-1)||(c1>=p1&&p2>=c2)){
        res=(res+solve(nums,ind+1,c1,c2)%MOD)%MOD;
        }
    }
    return dp[ind][p1+1][p2+1]=res%MOD;
}
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int ind=0,p1=-1, p2=-1;
        memset(dp,-1,sizeof(dp));
        long long res=solve(nums,ind,p1,p2)%MOD;
        return (int)res;
    }
};
