#define MOD 1000000007

class Solution {
private:
    int solver(int i, int prev, int n, vector<int>& nums, vector<vector<long long>> &dp){
        // Base Case
        if(i == n) return 1;
        if(dp[i][prev] != -1) return dp[i][prev];
        long long ans = 0;
        int prev2 = nums[i-1] - prev;
        for(int j = nums[i]; (prev2 >= (nums[i] - j)) && (j >= prev); j--){
            ans = (ans + 1ll*solver(i+1, j, n, nums, dp)) % MOD;
        }
        return dp[i][prev] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        // DP
        int n = nums.size();
        vector<vector<long long>> dp(n,vector<long long>(51, -1));
        int ans = 0;
        for(int i = 0; i <= nums[0]; i++){
            ans = (1ll*ans + 1ll*solver(1, i, n, nums, dp))%MOD;
        }
        return ans;
    }
};
