class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int M = 50;
        int MOD = int(1e9) + 7;
        vector<int> dp(M + 1);
        for(int i = 0; i < nums.size(); i++){
            vector<int> ndp(M + 1);
            for(int j = 0; j <= nums[i]; j++){
                if(i == 0){
                    ndp[j] = 1;
                    continue;
                }
                for(int k = 0; k <= j; k++){
                    int prv = nums[i - 1] - k;
                    if(prv < (nums[i] - j)) continue;
                    ndp[j] += dp[k];
                    ndp[j] %= MOD;
                }
            }            
            swap(dp, ndp);
        }
        int ret = 0;
        for(int i = 0; i < M + 1; i++) {
            ret += dp[i];
            ret %= MOD;
        }
        return ret;
    }
};
