class Solution {
int mod = 1e9+7;
public:
    int solve(int i , int prev1,int prev2,vector<int>&nums,vector< vector< vector<int>>>&dp){
        if(i>=nums.size())return 1;
        if(dp[i][prev1][prev2]!=-1)return dp[i][prev1][prev2];
        int res =0 ;
        for(int x=0;x<=nums[i];x++){
            if(x>=prev1 and nums[i]-x<=prev2){
                res=(res%mod+solve(i+1,x,nums[i]-x,nums,dp)%mod)%mod;
            }
        }
        return dp[i][prev1][prev2]=res;
    }
public:
    int countOfPairs(vector<int>& nums) {
        vector< vector< vector<int> > > dp (nums.size()+1,vector<vector<int>>(52,vector<int>(52,-1)));
        return solve(0,0,51,nums,dp);
    }
};
