class Solution {
public:
    int maxi = 0, mod = 1e9+7;
    long long dp[2002][52][52];
    long long rec(int i,vector<int>& nums,int prev1,int prev2){
        if(i == nums.size()){
            return 1LL*1;
        }
        if(dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];
        long long ans = 0;
        for (int p1 = prev1; p1 <= maxi; p1++) { 
            int p2 = nums[i] - p1;
            if (p2 >= 0 && p2 <= prev2) {  
                ans = (ans + rec(i + 1, nums, p1, p2)) % mod;
            }
        }
        return dp[i][prev1][prev2] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        maxi = *max_element(begin(nums),end(nums));
        memset(dp,-1,sizeof(dp));
        return rec(0,nums,0,maxi)%mod;
    }
};
