class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @cache
        def dp(i, j):
            if j >= n:
                return 1
            count = 0
            for k in range(i, nums[j] + 1):
                if nums[j] - k <= nums[j - 1] - i:
                    count += dp(k, j + 1)
            return count

        ans = 0
        for i in range(nums[0]+1):
            ans += dp(i, 1)

        return ans % (10**9 + 7)
