class Solution {
public:
int MOD=1e9+7;
    int helper(int curr,int prev1,int prev2,vector<int>& nums,vector<vector<vector<int>>>& dp){

        if (curr == nums.size()) return 1;

        if (dp[curr][prev1][prev2] != -1) return dp[curr][prev1][prev2]%MOD;

        int count = 0;
        for (int x = prev1; x <= nums[curr]; ++x) {
            int y = nums[curr] - x;
            if (y <= prev2) {
                count = (count%MOD + helper(curr + 1, x, y,nums,dp)%MOD) % MOD;
            }
        }

        return dp[curr][prev1][prev2] = count%MOD;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>>dp(nums.size()+1,vector<vector<int>>(51,vector<int>(52,-1)));
        int currSum=0;
        int count=0;
        for(int x=0;x<=nums[0];x++){
            int y=nums[0]-x;
            count=(count%MOD+helper(1,x,y,nums,dp)%MOD)%MOD;
        }
        return count%MOD;

    }
};
