class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = {}
        for i in range(nums[0] + 1):
            dp[i] = 1

        for i in range(1, len(nums)):
            curr = nums[i]
            newDp = {}

            s = 0
            currIndex = 0

            for x in range(curr + 1):
                y = curr - x
                prevY = nums[i - 1] - currIndex
                while currIndex <= x and prevY >= y and prevY in dp:
                    s = (s + dp[currIndex]) % (10 ** 9 + 7)
                    currIndex += 1
                    prevY = nums[i - 1] - currIndex
                newDp[x] = s

            dp = newDp
            # print(dp)

        return sum(dp.values()) % (10 ** 9 + 7)
