class Solution {
public:
    int mod {(int)1e9+ 7} ;
    int memo[2001][51] ;

    int solve(int idx, int last1, int last2, const vector<int> &nums ){
        if(idx == (int)nums.size()){
            return 1 ;
        }


        if(memo[idx][last1] != -1) return memo[idx][last1] ;

        int total {} ;

        for(int i {} ; i <= nums[idx] ; i++){
            if(i>= last1  && nums[idx] - i <= last2 ){
                total += solve(idx+1, i , nums[idx]- i , nums ) ;   
                total %=  mod ;
            }
        }

        return  memo[idx][last1] = total ;
    }
    int countOfPairs(vector<int>& nums) {
        memset(memo, -1 , sizeof(memo)) ;
        return solve(0 , 0 , 51 , nums ) ;
        return 0 ;
    }
};
