class Solution {
public:
    int mod = 1000000007;
    vector<vector<vector<int>>> tab;

    int help(int i, vector<int>& nums, int prev1, int prev2) {
        if (i == nums.size()) return 1;
        if (tab[i][prev1][prev2] != -1) return tab[i][prev1][prev2];

        int ans = 0;
        for (int num1 = 0; num1 <= nums[i]; num1++) {
            int num2 = nums[i] - num1;
            if (num1 >= prev1 && num2 <= prev2) {
                ans = (ans + help(i + 1, nums, num1, num2)) % mod;
            }
        }

        return tab[i][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        tab.resize(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return help(0, nums, 0, 50);
    }
};
