from typing import List
from functools import lru_cache

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        @lru_cache(None)
        def backtrack(current_index, prev_a, prev_b):
            if current_index == len(nums):
                return 1

            total_count = 0
            for a in range(nums[current_index] + 1):
                b = nums[current_index] - a
                if prev_a <= a and prev_b >= b:
                    total_count += backtrack(current_index + 1, a, b)
                    total_count %= mod

            return total_count

        return backtrack(0, -1, float('inf'))
