class Solution {
public:
    int dp[2001][51];
    const int mod=1e9+7;
    int func(int index,int prev,vector<int>&nums)
    {
        int n=nums.size();
        if(index>=n){
            return 1;
        }
        if(dp[index][prev]!=-1){
            return dp[index][prev];
        }

        int count=0;
        for(int i=0;i<=nums[index];i++)
        {
            int diff=index-1>=0?nums[index-1]-prev:51;
            if(prev<=i && (nums[index]-i)<=diff)
            { 
                count=(count+func(index+1,i,nums))%mod;   
            }    
        }

       return dp[index][prev]=count; 
    }

    int countOfPairs(vector<int>& nums)
    {
        memset(dp,-1,sizeof(dp)); 
        return func(0,0,nums);     
    }
};
