class Solution {
public:

    int t[2001][51][51];
    int MOD = 1e9 + 7;

    int helper(vector<int> &a, int i, int last1, int last2) {
        if(a.size() == i) return 1;

        if(t[i][last1][last2] != -1) return t[i][last1][last2];

        int m = 0;
        int res = 0;

        for(int j = 0; j <= a[i]; j++) {
            if(j < last1 || (a[i] - j) > last2) continue;
            res += helper(a, i+1, j, a[i] - j);
            res %= MOD;
        }

        return  t[i][last1][last2] = res;
    }

    int countOfPairs(vector<int>& a) {
        memset(t, -1, sizeof(t));
        return helper(a, 0, 0, 50);
    }
};
