class Solution {
public:
    int n=0;
    int dp[2001][51];
    int mod = 1e9+7;
    int rec(int level,int prev,vector<int> &nums)
    {
        if(level == n)
        {
            return 1;
        }

        if(dp[level][prev] != -1)
        {
            return dp[level][prev];
        }
        int ans = 0;
        int prev2 = nums[level-1] - prev;

        for(int i=prev;i<=nums[level];i++)
        {
            if(nums[level]-i > prev2)
            {
                continue;
            }
            ans = (ans%mod + rec(level+1,i,nums)%mod)%mod;
        }

        return dp[level][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(dp,-1,sizeof(dp));
        int ans = 0;
        for(int i=0;i<=nums[0];i++)
        {
            ans= (ans %mod + rec(1,i,nums)%mod)%mod;
        }
        return ans;
    }
};
