class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def rec(i, prev_a, prev_b):
            if i == n:
                return 1
            count = 0
            for a in range(prev_a, nums[i] + 1):
                b = nums[i] - a
                if b > prev_b:
                    continue
                count = (count + rec(i+1, a, b)) % mod
            return count
        n = len(nums)
        mod = 10 ** 9 + 7
        return rec(0, 0, inf)
