/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
    const MOD = 1e9 + 7;
    const n = nums.length;
    let memo = new Map();

    function generatePairs(index, lastArr1, lastArr2) {
        if (index === n) {
            return 1;
        }

        const key = `${index},${lastArr1},${lastArr2}`;
        if (memo.has(key)) {
            return memo.get(key);
        }

        let result = 0;

        for (let a = lastArr1; a <= nums[index]; a++) {
            let b = nums[index] - a;
            if (b <= lastArr2) {
                result = (result + generatePairs(index + 1, a, b)) % MOD;
            }
        }

        memo.set(key, result);
        return result;
    }

    return generatePairs(0, 0, nums[0]);
};
