class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
        int n=nums.size(),ans=0;
        constexpr int MOD=1e9+7;
        int dp[n][51][51];
        memset(dp,0,sizeof(dp));
        for(int i=0;i<=nums[0];i++)
        {
            dp[0][i][nums[0]-i]=1;
        }
        for(int len=1;len<n;len++)
        {
            for(int i=0;i<=nums[len];i++)
            {
                int j=nums[len]-i;
                for(int k=i;k<=50;k++)
                {
                    int l=nums[len-1]-k;
                    if(l<0||l>j)
                    {
                        continue;
                    }
                    dp[len][i][j]+=dp[len-1][k][l];
                    dp[len][i][j]%=MOD;
                }
            }
        }
        for(int i=0;i<=50;i++)
        {
            for(int j=0;j<=50;j++)
            {
                ans+=dp[n-1][i][j];
                ans%=MOD;
            }
        }
        return ans;
    }
};
