class Solution {
public:
    long long MOD = 1e9 + 7;
    long long help(int i , int l , int h , vector<int>&nums , vector<vector<vector<long long>>>&dp){
        int res = 0;
        if(i >= nums.size()){
            return 1;
        }
        if(dp[i][l][h]  != -1){
            return dp[i][l][h];
        }
        for(int j= l ;j <= nums[i];j++){
           for(int k = h; k >= 0;k--){
            if(j+k == nums[i]){
                res += help(i+1 , j,k,nums,dp);
                res = res%MOD;
            }
           }
        }
        return dp[i][l][h] = res;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();


        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51,-1)));

        return help(0,0,nums[0],nums,dp);
    }
};
