class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1000000007;
        int n = nums.size();
        map<pair<int,int>,int> m;
        m[{INT_MIN,INT_MAX}] = 1;
        for(int i:nums){
            // for(auto i:m){
            //     cout<<i.first.first<<" "<<i.first.second<<"->"<<i.second<<endl;
            // }
            map<pair<int,int>,int> m1;
            for(int j=0; j<=i; j++){
                for(auto x:m){
                    if(j>=x.first.first && (i-j)<=x.first.second){
                        m1[{j,i-j}] = 1LL*(m1[{j,i-j}]+x.second)%mod;
                    }
                }
            }
            m = m1;
        }      
        int result = 0;    
        for(auto i:m){
            result = 1LL*(result+i.second)%mod;
        }   
        return result; 
    }
};
