#include <iostream>
#include <vector>
#include <map>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<map<pair<int, int>, long long>> dp(n);

        for (int x = 0; x <= nums[0]; ++x) {
            int y = nums[0] - x;
            dp[0][{x, y}] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                dp[i][{x, y}] = 0;

                for (auto& prev : dp[i-1]) {
                    int prev_x = prev.first.first;
                    int prev_y = prev.first.second;
                    if (prev_x <= x && prev_y >= y) {
                        dp[i][{x, y}] = (dp[i][{x, y}] + prev.second) % MOD;
                    }
                }
            }
        }

        long long result = 0;
        for (auto& pair : dp[n-1]) {
            result = (result + pair.second) % MOD;
        }
        return result;
    }
};
