class Solution {
public:
    int n;
    const int mod = 1e9+7;

    int dp[2001][51][52];

    int solve(vector<int>& nums, int i, int prev1, int prev2){
        if(i == n) return 1;

        int ans = 0;

        if(dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];

        for(int x = prev1; x <= nums[i]; x++){
            int y = nums[i] - x;

            if(y > prev2) continue;

            // cout << "i: " << i << ", prev1: " << prev1 << ", prev2: " << prev2 << ", x: " << x << ", y: " << y << endl;

            ans = (ans + solve(nums, i+1, x, y)) % mod;
        }

        return dp[i][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();

        memset(dp, -1, sizeof(dp));

        return solve(nums, 0, 0, 51);
    }
};
