import java.util.Arrays;

public class Solution {
    private static final int MOD = 1000000007;

    public int countOfPairs(int[] values) {
        int size = values.length;
        if (size == 0) return 0; // Handle empty array case

        // Initialize memoization table
        long[][][] memo = new long[size][51][51];
        for (long[][] array2D : memo) {
            for (long[] array1D : array2D) {
                Arrays.fill(array1D, -1);
            }
        }

        return (int) solve(values, 0, 0, values[0], memo);
    }

    private long solve(int[] values, int index, int val1, int val2, long[][][] memo) {
        if (index >= values.length) return 1;

        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        long result = 0;

        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result = (result + solve(values, index + 1, i, j, memo)) % MOD;
                }
            }
        }

        memo[index][val1][val2] = result;
        return result;
    }
}
