class Solution {
    vector<vector<int>> dp;

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9 + 7;
        int lim = 51;

        dp = vector<vector<int>>(n, vector<int>(lim, 0));

        for (int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }

        for (int i = 1; i < n; i++) {

            for (int prev = 0; prev <= nums[i - 1]; prev++) {
                for (int cur = prev + max(nums[i] - nums[i - 1], 0); cur <= nums[i]; cur++) {
                    dp[i][cur] += dp[i - 1][prev];
                    dp[i][cur] %= mod;
                }
            }
        }

        int ans = 0;

        for (int cur = 0; cur <= nums[n - 1]; cur++) {
            ans += dp[n - 1][cur];
            ans %= mod;
        }

        return ans;
    }
};
