class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        dp = [collections.defaultdict(int) for _ in range(n)]
        for x in range(nums[0] + 1):
            dp[0][(x, nums[0] - x)] = 1
        for i in range(1, n):
            for x in range(nums[i] + 1):
                for y in range(x + 1):
                    if nums[i - 1] - y >= nums[i] - x:
                        dp[i][(x, nums[i] - x)] = (dp[i][(x, nums[i] - x)] + dp[i - 1][(y, nums[i - 1] - y)]) % mod
        res = 0
        for x in range(nums[n - 1] + 1):
            res = (res + dp[n - 1][(x, nums[n - 1] - x)]) % mod
        return res
