class Solution {
public:
    int func(int ind, int prev, vector<vector<int>>& dp, vector<int>& nums) {
        if (ind >= nums.size()) {
            return 1;
        }
        if (dp[ind][prev + 1] != -1) {
            return dp[ind][prev + 1];
        }

        int ans = 0;
        int n = nums[ind];
        const int MOD = 1e9 + 7;

        for (int i = 0; i <= n; i++) {
            if (prev==-1 || ((prev>=(n-i)&&(i>=(nums[ind-1]-prev))))) {
                ans = (ans + func(ind + 1, n - i, dp, nums)) % MOD;
            }
        }

        return dp[ind][prev + 1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int max_num = *max_element(nums.begin(), nums.end());
        vector<vector<int>> dp(nums.size()+1, vector<int>(max_num + 2, -1));
        return func(0, -1, dp, nums);
    }

};
