class Solution {
public:
    const int MOD = 1e9 + 7;
    int n;
      unordered_map<int, unordered_map<int, unordered_map<int, int>>> memo;
      int countPairs(int index, int last_arr1, int last_arr2, vector<int>& nums) {
        if (index == n) {
            return 1;
        }

        if (memo[index][last_arr1].find(last_arr2) != memo[index][last_arr1].end()) {
            return memo[index][last_arr1][last_arr2];
        }

        int result = 0;
        for (int x = last_arr1; x <= nums[index]; ++x) {
            int y = nums[index] - x;
            if (y <= last_arr2) {
                result = (result + countPairs(index + 1, x, y, nums)) % MOD;
            }
        }
        memo[index][last_arr1][last_arr2] = result;
        return result;
    }
    int countOfPairs(vector<int>& nums) {
          n = nums.size();
        return countPairs(0, 0, nums[0], nums);
    }
};
