class Solution {
public:
    int dp[52][2001];
    int mod = 1e9+7;
    int f(int maxi, int idx, vector<int> &nums){
        int n = nums.size();
        if(idx == n) return 1;
        if(dp[maxi+1][idx] != -1) return dp[maxi+1][idx];
        int res=0;
        for(int i=maxi; i<=nums[idx]; i++){
            if(idx){
                // cout << res <<  " " << maxi << " " << idx << endl;
                if((nums[idx-1]-maxi >= nums[idx]-i) && (nums[idx]-i >= 0)){
                    res += f(i, idx+1, nums);
                }
            }
            else{
                res += f(i, idx+1, nums);
            }
            res %= mod;
        }
        return dp[maxi+1][idx] = res;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return f(0, 0, nums);
    }
};
