class Solution {
public:
    int mod = 1e9 + 7;
    int f(int i, int left, int right, vector<int> &nums, vector<vector<vector<long>>> &dp){
        if(i == nums.size())return 1;
        if(dp[i][left][right]!=-1)return dp[i][left][right];
        int val_l = left;
        long ans = 0;
        while(val_l<=nums[i]){
            int val_r = nums[i]-val_l;
            if(val_r<=right){
                ans += (long)f(i+1, val_l, val_r, nums, dp);
                ans %= mod;
            }
            val_l++;
        }
        return dp[i][left][right]=ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long>>> dp(n, vector<vector<long>>(52, vector<long>(52, -1)));
        return f(0, 0, 51, nums, dp);
    }
};
