class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # n[i]>=a[i] >= a[i-1] + min(0,n[i]-n[i-1])
        out = 0
        @cache
        def dfs(a_prev, i):
            if i>=len(nums): 
                # print(a_prev)
                return 1
            dn =  0 if i==0 else nums[i]-nums[i-1]
            a_curr_min = a_prev  + max(0, dn)
            a_curr_max = nums[i]
            n_comb = 0 
            for a_curr in range(a_curr_min, a_curr_max+1): 
                n_comb += dfs(a_curr, i+1) 
            return n_comb%(10**9 + 7)
        return dfs(0,0)
