#define MOD 1000000007

int countOfPairs(int* nums, int numsSize) {
    int dp[numsSize][51];
    int result = 0;

    for (int i = 0; i < numsSize; i++) {
        for (int j = 0; j <= 50; j++) {
            dp[i][j] = 0;
        }
    }

    for (int j = 0; j <= nums[0]; j++) {
        dp[0][j] = 1;
    }

    // Fill the DP table
    for (int i = 1; i < numsSize; i++) {
        for (int j = 0; j <= nums[i]; j++) {
            int arr2_val = nums[i] - j;
            if (arr2_val >= 0 && arr2_val <= 50) {
                for (int k = 0; k <= j; k++) {  // ensure arr1[i-1] <= arr1[i]
                    if (arr2_val <= nums[i-1] - k) {  // ensure arr2[i] <= arr2[i-1]
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
        }
    }

    for (int j = 0; j <= 50; j++) {
        result = (result + dp[numsSize-1][j]) % MOD;
    }

    return result;
}
