#include <vector>
#include <functional>

class Solution {
public:
    int countOfPairs(std::vector<int>& values) {
        int size = values.size();
        std::vector<std::vector<std::vector<long long>>> memo(size, std::vector<std::vector<long long>>(51, std::vector<long long>(51, -1)));

        std::function<long long(int, int, int)> solve = [&](int index, int val1, int val2) -> long long {
            if (index >= values.size()) return 1;
            long long result = 0;
            long long mod_val = 1e9 + 7;
            if (memo[index][val1][val2] != -1) return memo[index][val1][val2];
            for (int i = val1; i <= values[index]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == values[index]) {
                        result += solve(index + 1, i, j);
                        result %= mod_val;
                    }
                }
            }
            return memo[index][val1][val2] = result;
        };

        return solve(0, 0, values[0]);
    }
};
