class Solution {
public:

    const int MOD = 1e9 + 7;

    int countPairsUtil(vector<int>& nums, int i, int prevArr1, int prevArr2, vector<vector<vector<int>>>& memo) {
    if (i == nums.size()) return 1;  

    if (memo[i][prevArr1][prevArr2] != -1) return memo[i][prevArr1][prevArr2];

    int count = 0;

    for (int arr1Value = prevArr1; arr1Value <= nums[i]; ++arr1Value) {
        int arr2Value = nums[i] - arr1Value;
        if (arr2Value <= prevArr2) { 
            count = (count + countPairsUtil(nums, i + 1, arr1Value, arr2Value, memo)) % MOD;
        }
    }

    return memo[i][prevArr1][prevArr2] = count;
}
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> memo(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return countPairsUtil(nums, 0, 0, 50, memo);
    }
};
