class Solution {
public:
    using LL = long long;
    const LL MOD = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        // vector<LL> dp(n, 0);
        LL dp[n][55];
        memset(dp, 0, sizeof(dp));
        for (int k=0; k<=50; k++) {
            int x = nums[0];
            if (x-k >= 0)
                dp[0][k] = 1;
        }
        for (int i=1; i<n; i++) {
            int x = nums[i];
            int y = nums[i-1];
            for (int j=0; j<=50; j++) {

                for (int k=0; k<=50; k++) {

                    if (k<=j && y-k >= x-j && y-k>=0 && x-j >=0) {
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= MOD;
                    }

                }
            }
        }
        LL ans = 0;
        for (int k=0; k<=50; k++) {
            ans += dp[n-1][k];
            ans %= MOD;
        }
        return ans;
    }
};
