class Solution {
public:
    int mod=1000000007;
    int dp[2001][51][51];
    int Find(int idx ,int p1 , int p2 ,vector<int> &nums)
    {
        if(idx == nums.size()) return 1;

        if(dp[idx][p1][p2] != -1) return dp[idx][p1][p2];
        int ans=0;
        for(int l=p1;l<=nums[idx];l++)
        {
            if(nums[idx]-l <= p2)
            {
                ans=(ans +Find(idx+1 ,l ,nums[idx]-l ,nums))%mod;
            }
        }
        return dp[idx][p1][p2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp ,-1 ,sizeof(dp));
        int ans=0;
        for(int i=0;i<=nums[0];i++)
        {
            ans=(ans + Find(1 ,i ,nums[0]-i ,nums))%mod;
        }
        return ans;
    }
};
