class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        MOD = 10**9 + 7
        n = len(nums)
        dp = [[0 for _ in range(51)] for i in range(n)]
        for k in range(nums[0]+1):
            dp[0][k] = 1
        for i in range(1,n):
            for k in range(nums[i]+1):
                currA1 = k
                currA2 = nums[i] - currA1
                for x in range(k+1):
                    prevA1 = x
                    prevA2 = nums[i-1] - prevA1
                    if(currA2 <= prevA2):
                        dp[i][k] += dp[i-1][x]

        ans = sum(dp[-1])
        return ans%MOD
