class Solution {
private:
    const int mod = 1e9+7;
    int dp[2001][51][51];
    int solve(int i, int prevArr1, int prevArr2, vector<int> &nums) {
        if (i == nums.size()) return 1;
        if (dp[i][prevArr1][prevArr2] != -1) return dp[i][prevArr1][prevArr2];

        int ans = 0;

        for (int val = prevArr1; val <= nums[i]; val++) {
            int rem = nums[i] - val;
            if (prevArr2 >= rem) {
                int res = solve(i+1, val, rem, nums);
                ans = (ans%mod + res%mod)%mod;
            }
        }

        return dp[i][prevArr1][prevArr2] = ans;
    }
public:
    int countOfPairs(vector<int> &nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));

        int ans = 0;

        for (int val = 0; val <= nums[0]; val++) {
            int res = solve(1, val, nums[0] - val, nums);
            ans = (ans%mod + res%mod)%mod;
        }

        return ans;
    }
};
