class Solution {
    public Integer[][][] dp ;
    public int mod = (int)(1e9 + 7);
    public int countOfPairs(int[] nums) {
        int n = nums.length ;
        dp = new Integer[n][51][51] ;
        return helper(nums, 0, 0, 50);
    }
    public int helper(int[]nums, int idx, int num1, int num2){
        if(idx == nums.length){
            // System.out.println(num1 + "-->" + num2);
            return 1 ;
        }
        if(dp[idx][num1][num2] != null)return dp[idx][num1][num2] ;
        int rr = 0 ;
        for(int i = num1 ; i <= nums[idx] ; i++){
            if(num2 >= (nums[idx] - i)){
               rr = (rr + helper(nums, idx + 1, i, nums[idx] - i)) % mod ;
            }
        }
        return dp[idx][num1][num2] = rr ;
    }
}
