class Solution {
public:
    const int Mod = (1e9 + 7);
    vector<vector<vector<int>>> dp;
    int solver(vector<int>& nums, int idx, int incr, int desc) {
        if(idx >= nums.size()) {
            return 1;
        }

        if(dp[idx][incr][desc] != -1) return dp[idx][incr][desc];
        long long int total = 0;
        for(int i=0; i<=nums[idx]; ++i) {
            int first = i;
            int second = nums[idx] - i;
            if(first >= incr && second <= desc) {
                total = (total + solver(nums, idx+1, first, second)) % Mod;
            }
        }
        return dp[idx][incr][desc] = total;

    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size(), vector<vector<int>>(52, vector<int>(52, -1)));
        return solver(nums, 0, 0, 51);
    }
};
