class Solution {
public:
    int md = 1e9 + 7;
    long long calc(int pos, int val, vector<int>& nums, vector<vector<long long>>& dp) {
        if(pos == nums.size()) {
            return 1; 
        }

        if(val > 50) {
            return 0;
        }

        if(dp[pos][val] != -1) {
            return dp[pos][val];
        }


        long long ret = 0;

        // I am in pos, I am allowed to have vals >= i; for arr1[i], my arr2[i] would be nums[i]-arr1[i]; 
        int prev = INT_MAX;
        if(pos != 0) {
            prev = nums[pos-1]-val;
        }

        for(int i=val;i<=nums[pos];i++) {

            int arr2Val = nums[pos]-i; 

            if(arr2Val > prev) {
                // cout<<"No for : "<<pos<<" "<<i<<" when prev val is : "<<val<<"\n";
                continue;
            }
            // cout<<"From State : {"<<pos<<","<<val<<"} and To State : {"<<pos+1<<","<<i<<"}\n";
            ret += calc(pos+1, i, nums, dp);

            ret = ret % md;
        }
        // cout<<pos<<" "<<val<<" "<<ret<<"\n\n";
        return dp[pos][val] = ret;

    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<long long>>dp(n, vector<long long>(51, -1));

        // start with 0,0
        long long ret = 0;
        // for(int i=0;i<=0;i++) {
            ret += calc(0,0,nums,dp);
        // }

        return ret;

    }
};
