class Solution {
public:
    int mod = 1e9+7;
    long long dp[2001][51][51];
    long long solve(vector<int>& nums, int i = 0, int n1 = 0, int n2 = 50) {
        if(i >= size(nums)) return 1;
        if(dp[i][n1][n2] != -1) return dp[i][n1][n2];

        long long ans = 0;
        for(int x = n1; x <= 50; ++x) {
            for(int y = n2; y >= 0; --y) {
                if(x + y == nums[i]) ans = (ans%mod + solve(nums, i+1, x, y)%mod)%mod;
            }
        }

        return dp[i][n1][n2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(nums);
    }
};
