class Solution {

    int MOD = 1000_000_007;
    long[][] dp;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        dp = new long[n][52];

        for(long[] arr : dp) {
            Arrays.fill(arr, -1);
        }

        long ans = dfs(0, 0, n, nums);

        return (int)ans;
    }

    long dfs(int i, int p, int n, int[] a) {
        if(i >= n) {
            return 1;
        }

        if(dp[i][p] != -1) {
            return dp[i][p];
        }

        long ans = 0;

        int p1 = p;
        int p2 = i == 0 ? 50 : a[i-1] - p;
        for(int j = 0; j <= a[i]; j++) {
            if(j < p1) {
                continue;
            }

            if(a[i]-j > p2) {
                continue;
            }

            long cur = dfs(i+1, j, n, a);
            ans = (ans + cur)%MOD;
        }

        dp[i][p] = ans;
        return ans;
    }
}
