class Solution {
public:
const int M=1e9+7;
    long long recursive(vector <int>& nums,int i,int u,int v,vector <vector <vector <long long>>>& dp){
        if(i==nums.size()){
            return 1LL;
        }
        if(dp[i][u][v]!=-1LL){
            return dp[i][u][v];
        }
        long long res=0;
        for(int j=u;j<=nums[i];j++){
            if(nums[i]-j<=v){
                res+=recursive(nums,i+1,j,min(v,nums[i]-j),dp);
                res%=M;
            }
        }
        return dp[i][u][v]=res;
    }
    int countOfPairs(vector<int>& nums) {
        vector <vector <vector <long long>>> dp(nums.size(),vector <vector <long long>> (51,vector <long long> (51,-1)));
        return recursive(nums,0,0,50,dp);
    }
};
