class Solution {
public:
    int n;
    vector<vector<int>> dp;
    long long int mod = 1000000007;
    vector<int> nums;
    int solve(int i,int a){
        if (i == n)
            return 1;
        int &ans = dp[i][a];
        if (ans != -1)
            return ans;
        ans = 0;
        int lasta = 0, lastb = 50;

        if (i){
            lasta = a;
            lastb = nums[i-1] - a;
        }

        for (int x=0;x<=nums[i];x++){
            if (x >= lasta and nums[i]-x<=lastb)
            ans = (ans + solve(i + 1, x))%mod;
        }
        return ans;
    }
    int countOfPairs(vector<int>& arr) {
        nums = arr;
        n = nums.size();
        dp = vector<vector<int>> (n  , vector<int> (51, -1));
        return solve(0 , 0);
    }
};
