class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = 10 ** 9 + 7
        @functools.cache
        def util(i, a, b):
            # print(i, a, b)
            # nonlocal ans
            if i == n:
                return 1
            curr = 0
            for j in range(nums[i] + 1):
                if i == 0 or (j >= a and nums[i] - j <= b):
                    # if i == 3:
                        # print(j, nums[i] -j , a, b)
                    curr += util(i + 1, j, nums[i] - j)
            return curr
        return util(0, -1, -1) % mod
