class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[0 for _ in range(51)] for _ in range(len(nums))]
        mod = int(1e9+7)
        def add(a,b):return ((a % mod) + (b % mod)) % mod
        for i in range(len(nums) - 1,-1,-1):
            for j in range(nums[i] + 1):
                b = nums[i] - j
                if i + 1 == len(nums):
                    dp[i][j] = 1
                else:
                    for k in range(nums[i + 1] + 1):
                        nb = nums[i + 1] - k
                        if  j <= k and b >= nb:
                            dp[i][j] = add(dp[i][j],dp[i + 1][k])
        ans = 0
        for i in range(51):
            ans = add(ans,dp[0][i])
        return ans

