class Solution {
public:
    const int mod=1e9+7;
    int solve(int i,int prev,vector<int>& nums, vector<vector<int>> &dp){
        if(i==nums.size()) return 1;
        if(dp[i][prev]!=-1) return dp[i][prev];
        int ans=0;
        for(int curr=0;curr<=nums[i];curr++){
            if(prev==51||(curr>=prev&&(nums[i]-curr)<=nums[i-1]-prev)){
                ans = (ans + solve(i+1,curr,nums,dp))%mod;
            }
        }
        return dp[i][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(n+1,vector<int>(52,-1));
        return solve(0,51,nums,dp);
    }
};
