class Solution {
public:
    #define ll long long
    #define mod 1000000007

    int countOfPairs(vector<int>& a) {
        int n = a.size() ;
        vector<vector<int>> dp(n + 1, vector<int>(51, 0));

        dp[0][0] = 1 ;

        for(int i = 1 ; i <= n; i ++){
            for(int j = 0 ;  j <= 50; j ++){
                int el = a[i - 1] - j ;
                if(el < 0) break;

                for(int k = 0 ; k <= j; k ++){
                    int prev = 1e9 ;
                    if(i > 1) prev = a[i - 2] - k ;

                    if(prev < el) continue;

                    dp[i][j] += dp[i - 1][k] ;
                    dp[i][j] %= mod ;
                }
            }
        }

        int ans = 0 ;
        for(int i = 0 ; i <= 50; i ++) ans = (ans + dp[n][i]) % mod ;

        return ans ;
    }
};
