class Solution {
    int dp[2005][55][55];
    const int M=1e9+7;
    int fun(vector<int>&num,int l1,int l2,int ind){
        if(ind>=num.size())
            return 1;
        int ans=0;
        if(dp[ind][l1][l2]!=-1)
            return dp[ind][l1][l2];
        for(int i=0;i<=num[ind];i++){
            if(i>=l1 and num[ind]-i<=l2)
                ans=(ans%M+fun(num,i,num[ind]-i,ind+1)%M)%M;
        }
        return dp[ind][l1][l2]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int l1=0,l2=51;
        memset(dp,-1,sizeof(dp));
        int ans=fun(nums,l1,l2,0);
        return ans;

    }
};
