class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<long long>> cnts(n, vector<long long>(51));
        for(int i = 0; i <= nums[0]; ++i)
          cnts[0][i] =  1;
        constexpr int M = 1e9 + 7;
        for(int i = 1; i < n; ++i) {
          int curr  = nums[i];
          int prev = nums[i-1];
          for(int v = 0; v <= curr; ++v) {
            int b_curr = curr - v;
            for(int p = 0; p <= v; ++p) {
              int b_prev = prev - p;
              if (cnts[i-1][p] == 0 || b_curr > b_prev) continue;
              cnts[i][v] = (cnts[i][v] + cnts[i-1][p]) % M;
            }
          }
        }

        return accumulate(cnts.back().begin(), cnts.back().end(), 0ll) % M;
    }
};
